/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER log_usuariomodificar
BEFORE UPDATE ON usuarios
FOR EACH ROW
BEGIN
    IF OLD.nombreUsuario <> NEW.nombreUsuario THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES (CONCAT('Cambio en nombreUsuario: ', OLD.nombreUsuario, ' a ', NEW.nombreUsuario), NOW());
    END IF;

    IF OLD.Nombre <> NEW.Nombre THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES (CONCAT('Cambio en Nombre: ', OLD.Nombre, ' a ', NEW.Nombre), NOW());
    END IF;

    IF OLD.Apellido <> NEW.Apellido THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES (CONCAT('Cambio en Apellido: ', OLD.Apellido, ' a ', NEW.Apellido), NOW());
    END IF;

    IF OLD.Contrasena <> NEW.Contrasena THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES ('Cambio en Contrasena', NOW()); -- Por seguridad, no registrar la contrase√±a en texto plano
    END IF;

    IF OLD.FechaNacimiento <> NEW.FechaNacimiento THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES (CONCAT('Cambio en FechaNacimiento: ', OLD.FechaNacimiento, ' a ', NEW.FechaNacimiento), NOW());
    END IF;

    IF OLD.Poblacion <> NEW.Poblacion THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES (CONCAT('Cambio en Poblacion: ', OLD.Poblacion, ' a ', NEW.Poblacion), NOW());
    END IF;

    IF OLD.ImagenPerfil <> NEW.ImagenPerfil THEN
        INSERT INTO logs (Accion, `Fecha-Hora`)
        VALUES ('Cambio en ImagenPerfil', NOW()); -- Por seguridad, no registrar la imagen en texto plano
    END IF;
END */;;
